SELECT ALOCD.ORDER_RELEASE_GID
,ALOCD.COST_DESCRIPTION
,ALOCD.ACCESSORIAL_CODE_GID
,ROUND(ALOCD.COST,5)			COST
,ALOCD.COST_CURRENCY_GID
,ROUND(case when (ALOCD.COST_CURRENCY_GID = 'EUR' OR ALOCD.COST_CURRENCY_GID IS null) THEN ALOCD.cost
 				when ALOCD.COST_CURRENCY_GID <> 'EUR' THEN ALOCD.cost *
 				unilever.ebs_procedures_ule.get_quarterly_ex_rate(nvl(sh.start_time,SYSDATE-60),ALOCD.COST_CURRENCY_GID,'EUR')
